import React, { useState } from 'react';
import { Box, Button, Input, Text, Center } from '@mantine/core';
import { fireAuth } from '../firebase';

const CircleCreateForm = () => {
  const [circleName, setCircleName] = useState('');
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');

  const handleSubmit = async (e) => {
    e.preventDefault();
    if (!circleName) {
      setError('サークル名を入力してください');
      return;
    }

    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/postcircle', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleName, creater: fireAuth.currentUser?.uid }), // currentUserIdは適切なユーザーIDに置き換えてください
      });

      if (response.ok) {
        const result = await response.json();
        setSuccess('サークルが作成されました');
        setCircleName('');
        setError('');
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの作成に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの作成に失敗しました');
      setSuccess('');
    }
  };

  return (
    <Center style={{ height: '100vh' }}>
      <Box>
        <form onSubmit={handleSubmit}>
          <Input
            placeholder="サークル名"
            value={circleName}
            onChange={(e) => setCircleName(e.target.value)}
            mb="md"
          />
          {error && <Text color="red">{error}</Text>}
          {success && <Text color="green">{success}</Text>}
          <Button type="submit">サークル作成</Button>
        </form>
      </Box>
    </Center>
  );
};

export default CircleCreateForm;
