import React, { useState } from 'react';
import ReplyForm from './ReplyForm.tsx';
import ReplyList from './ReplyList.tsx';

interface ReplyProps {
  tweetId: string;
}

const Reply: React.FC<ReplyProps> = ({ tweetId }) => {
  const [showReplyForm, setShowReplyForm] = useState(false);

  const handleReplyPosted = () => {
    setShowReplyForm(false);
  };

  return (
    <div>
      <button onClick={() => setShowReplyForm(!showReplyForm)}>返信</button>
      {showReplyForm && <ReplyForm tweetId={tweetId} onReplyPosted={handleReplyPosted} />}
      <ReplyList tweetId={tweetId} />
    </div>
  );
};

export default Reply;
