import { fireAuth } from '../firebase.ts';
import React, { useState, useEffect } from 'react';
import Contentform from './Newpost.tsx';
import { getPosts } from './Postapi.tsx';
import { Link, useNavigate } from 'react-router-dom';

interface Post {
  id: string;
  content: string;
  likes: number;
}

const Post_App: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [profileData, setProfileData] = useState<{ name: string; age: number; username: string; avatar: string } | null>(null);
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);

  useEffect(() => {
    const fetchPosts = async () => {
      setPosts(posts);
    };

    const fetchUserProfile = async () => {
      if (fireAuth.currentUser) {
        const response = await fetch(
          `https://hackathon-ro2txyk6rq-uc.a.run.app/search?uid=${fireAuth.currentUser?.uid}`
        );
        const data = await response.json();
        console.log(data);
        if (data && data.length > 0) {
          setProfileData(data[0]);
        } else {
          setProfileData(null);
        }
      }
    };

    fetchPosts();
    fetchUserProfile();
  }, []);

  const createPost = async (content: string) => {
    return new Promise<void>(async (resolve, reject) => {
      const firebaseUID = fireAuth.currentUser?.uid;
      let errormessage = "";
      if (!content) {
        alert("tweet is empty");
        errormessage = "tweet is empty";
      }
      if (errormessage) {
        console.log(errormessage);
        reject(errormessage);
        return;
      }
      try {
        const response = await fetch(
          "https://hackathon-ro2txyk6rq-uc.a.run.app/post",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              content: content,
              uid: firebaseUID,
            }),
          }
        );
        if (!response.ok) {
          const errorData = await response.json();
        console.error("Failed to tweet:", errorData);
          alert("failed to tweet");
          errormessage = "failed to tweet";
          reject(errormessage);
          return;
        } else {
          console.log(firebaseUID);
          resolve();
        }
      } catch (error) {
        console.error("failed tweet", error);
        alert("failed tweet");
        reject(error);
      }
    });
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  const handleProfileClick = () => {
    navigate("/profile");
  };

  const handleSearch = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const query = e.target.value;
    setSearchTerm(query);
    if (query.length > 0) {
      const response = await fetch(
        `https://hackathon-ro2txyk6rq-uc.a.run.app/searchUsers?username=${query}`
      );
      const data = await response.json();
      setSearchResults(data);
    } else {
      setSearchResults([]);
    }
  };

  return (
    <div>
      <h1>Twitter Clone</h1>
      <input
        type="text"
        placeholder="ユーザーネームを検索"
        value={searchTerm}
        onChange={handleSearch}
        style={{ width: '50%', margin: '0 auto', display: 'block' }}
      />
      <div>
        {searchResults.map(user => (
          <div key={user.uid}>
            <p>{user.username}</p>
          </div>
        ))}
      </div>
      <div className="sidebar">
        <img src={profileData.avatar} alt="Profile" onClick={handleProfileClick} style={{ cursor: 'pointer' }} />
      </div>
      <Contentform onNewPost={createPost} />
      <div>
        {posts.map(post => (
          <div key={post.id}>
            <p>{post.content}</p>
            <p>{post.likes} Likes</p>
          </div>
        ))}
      </div>
    </div>
  );
};


export default Post_App;
