import React, { useState } from 'react';
import { signOut,onAuthStateChanged } from 'firebase/auth';
import { fireAuth} from '../firebase.ts';

interface ProfileSetupProps {
  onProfileComplete: () => void;
}

const ProfileSetup: React.FC<ProfileSetupProps> = ({ onProfileComplete }) => {
  const [name, setName] = useState<string>('');
  const [age, setAge] = useState<number>(0);
  const [username, setUsername] = useState<string>('');

  const handleSubmit = async (e: React.FormEvent) => {
    e.preventDefault();
    // プロフィール情報を保存（バックエンドAPIを使用する場合、ここでAPI呼び出しを行う）
    // await api.saveProfile({ name, age, username });
    onProfileComplete();
  };

  const signOutWithGoogle = (): void => {
    signOut(fireAuth).then(() => {
      alert("ログアウトしました");
    }).catch(err => {
      alert(err);
    });
  };

  const [loginUser, setLoginUser] = useState(fireAuth.currentUser);
  onAuthStateChanged(fireAuth, user => {
    setLoginUser(user);
  });


  return (
    <div>
    <form onSubmit={handleSubmit}>
      <input
        type="text"
        value={name}
        onChange={e => setName(e.target.value)}
        placeholder="Name"
      />
      <input
        type="number"
        value={age}
        onChange={e => setAge(Number(e.target.value))}
        placeholder="Age"
      />
      <input
        type="text"
        value={username}
        onChange={e => setUsername(e.target.value)}
        placeholder="Username"
      />
      <button type="submit">Save Profile</button>
    </form>
    <button className="logoutform"onClick={signOutWithGoogle}>
    ログアウト
    </button>
    </div>
  );
};

export default ProfileSetup;
