import React, { useState } from 'react';
import { fireAuth } from '../firebase.ts';

interface ReplyFormProps {
  tweetId: string;
  onReplyPosted: () => void;
}

const ReplyForm: React.FC<ReplyFormProps> = ({ tweetId, onReplyPosted }) => {
  const [replyContent, setReplyContent] = useState('');

  const handleReplyPost = async () => {
    if (!replyContent) {
      alert('Reply content is empty');
      return;
    }

    const reply = {
      replycontent: replyContent,
      uid: fireAuth.currentUser?.uid,
      tweetid: tweetId,
      username: fireAuth.currentUser?.uid,
    };

    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/reply', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reply),
      });

      if (response.ok) {
        console.log(reply)
        setReplyContent('');
        onReplyPosted();
      } else {
        alert('Failed to post reply');
      }
    } catch (error) {
      console.error('Error posting reply:', error);
    }
  };

  return (
    <div>
      <textarea
        value={replyContent}
        onChange={(e) => setReplyContent(e.target.value)}
        placeholder="Write your reply..."
      />
      <button onClick={handleReplyPost}>Reply</button>
    </div>
  );
};

export default ReplyForm;
