import React, { useState } from 'react';
import { signOut,onAuthStateChanged } from 'firebase/auth';
import { fireAuth} from '../firebase.ts';
import Profileform from './Profileform.tsx';

interface ProfileSetupProps {
  onProfileComplete: () => void;
}

const ProfileSetup: React.FC<ProfileSetupProps> = ({ onProfileComplete }) => {

  const [name, setName] = useState<string>('');
  const [age, setAge] = useState<number>(0);
  const [username, setUsername] = useState<string>('');

  //profile登録
  const handleSubmit = async (name: string, age: string, username: string):Promise<void> => {
    if (!name || !age || !username){
      throw new Error("name or age or username is empty");
    }

    if (name.length > 50) {
      alert("Please enter a name shorter than 50 characters");
      return;
    }
    const age2 = Number(age)

    if (age2 < 20 || age2 > 80) {
      alert("Please enter age between 20 and 80");
      return;
    }

    if (username.length > 20){
      alert("Please enter a username shorter than 20 characters")
    }
    try{
      const response = await fetch(
      "https://hackathon-ro2txyk6rq-uc.a.run.app/register",{
        method: "POST",
        headers: {
          "Content-Type": "application/json",
        },
        body: JSON.stringify({
          name:name,
          age:age2,
          username:username,
        }),
      });
    if (!response.ok) {
      throw new Error("failed to POST")
    }

    }catch(error){
      console.error("failed POSTing",error)
  }
  onProfileComplete();
};

//ログアウト
  const signOutWithGoogle = (): void => {
    signOut(fireAuth).then(() => {
      alert("ログアウトしました");
    }).catch(err => {
      alert(err);
    });
  };

  //監視
  const [loginUser, setLoginUser] = useState(fireAuth.currentUser);
  onAuthStateChanged(fireAuth, user => {
    setLoginUser(user);
  });


  return (
    <div>
    <form onSubmit={handleSubmit}>
      <button type="submit">Save Profile</button>
    </form>
    <button onClick={signOutWithGoogle}>
    ログアウト
    </button>
    </div>
  );
};

export default ProfileSetup;
