import { fireAuth } from '../firebase.ts';
import React, { useState, useEffect, useCallback } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Text, Center, Grid, Button, Loader, Textarea, FileInput, Image, Avatar } from '@mantine/core';
import { getStorage, ref, uploadBytes, getDownloadURL } from 'firebase/storage';
import defaultAvatar from '../assets/default_user.png';
import HashtagText from './Hashtag.tsx';
import LikeButton from './Like_post.tsx';
import ReplyList from './ReplyList.tsx';
import ReplyForm from './ReplyForm.tsx';

interface CircleTweet {
  circleid: string;
  tweetid: string;
  username: string;
  time: string;
  content: string;
  uid: string;
  like: number;
  isLiked: boolean;
  replyCount: number;
  avatar_url: string;
  image?: string;
}

interface MyProfileProps {
  profileData: { name: string; age: number; username: string; avatar: string };
}

const CirclePage: React.FC<MyProfileProps> = ({ profileData }) => {
  const { circleid } = useParams<{ circleid: string }>();
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMember, setIsMember] = useState<boolean>(false);
  const [isCreator, setIsCreator] = useState<boolean>(false);
  const [tweetImage, setTweetImage] = useState<File | null>(null);
  const [imagePreview, setImagePreview] = useState<string | null>(null);
  const [imageOk, setImageOk] = useState<boolean>(false);
  const [allTweets, setAllTweets] = useState<CircleTweet[]>([]);
  const [replyContent, setReplyContent] = useState<string>('');

  useEffect(() => {
    const fetchCircleDetails = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcircle?uid=${fireAuth.currentUser?.uid}`);
        if (response.ok) {
          const data = await response.json();
          for (let i = 0; i < data.length; i++) {
            if (circleid === data[i].circleid) {
              setIsMember(true);
              if (data[i].creater === fireAuth.currentUser?.uid) {
                setIsCreator(true);
              }
            }
          }
        }
      } catch (error) {
        console.log(error);
      }
    };

    const fetchCircleTweets = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcircletweets?circleid=${circleid}`);
        if (response.ok) {
          const data = await response.json();
          setAllTweets(data);
        }
      } catch (err) {
        setError('サークルの取得に失敗しました');
      } finally {
        setLoading(false);
      }
    };

    fetchCircleDetails();
    fetchCircleTweets();
  }, [circleid]);

  const handleCircleRegister = async () => {
    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/postcirclemember', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleID: circleid, uid: fireAuth.currentUser?.uid }),
      });

      if (response.ok) {
        setSuccess('サークルに加入しました');
        setError('');
        setIsMember(true); // 加入後に脱退ボタンを表示
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの加入に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの加入に失敗しました');
      setSuccess('');
    }
  };

  const handleCircleUnregister = async () => {
    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/stopcircle', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleid: circleid, uid: fireAuth.currentUser?.uid }),
      });

      if (response.ok) {
        setSuccess('サークルを脱退しました');
        setError('');
        setIsMember(false); // 脱退後に加入ボタンを表示
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの脱退に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの脱退に失敗しました');
      setSuccess('');
    }
  };

  const uploadImageAndGetUrl = async (file: File): Promise<string> => {
    const storage = getStorage();
    const storageRef = ref(storage, `tweet_images/${file.name}`);
    await uploadBytes(storageRef, file);
    return await getDownloadURL(storageRef);
  };

  const handleImageChange = (file: File | null) => {
    if (file) {
      setTweetImage(file);
      const reader = new FileReader();
      reader.onloadend = () => {
        setImagePreview(reader.result as string);
      };
      reader.readAsDataURL(file);
    } else {
      setTweetImage(null);
      setImagePreview(null);
    }
  };

  const createPost = async (content: string) => {
    setImageOk(true);
    const firebaseUID = fireAuth.currentUser?.uid;
    if (!content) {
      content = "";
    }
    try {
      let image_url = "";
      if (tweetImage) {
        image_url = await uploadImageAndGetUrl(tweetImage);
      }

      const avatarresponse = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/searchAvatar?uid=${firebaseUID}`);
      const avatarData = await avatarresponse.json();
      const response = await fetch(
        "https://hackathon-ro2txyk6rq-uc.a.run.app/circlepost",
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
          },
          body: JSON.stringify({
            circleid: circleid,
            username: profileData?.username,
            content: content,
            uid: firebaseUID,
            like: 0,
            image: image_url,
          }),
        }
      );
      if (!response.ok) {
        const errorData = await response.json();
        console.error("Failed to tweet:", errorData);
        alert("failed to tweet");
        return;
      } else {
        const newTweet: CircleTweet = {
          circleid: circleid || "",
          tweetid: "",
          username: profileData?.username || "",
          time: new Date().toISOString(),
          content: content,
          uid: firebaseUID || "",
          like: 0,
          isLiked: false,
          replyCount: 0,
          avatar_url: avatarData.avatar_url || "",
          image: image_url || ""
        };
        setAllTweets(prevTweets => [newTweet, ...prevTweets]);
        setTweetImage(null);
        setImagePreview(null);
        setImageOk(false);
      }
    } catch (error) {
      console.error("failed tweet", error);
      alert("failed tweet");
      setImageOk(false);
    }
  };

  const formatDateTime = (dateTime: string) => {
    const date = new Date(dateTime);
    const formattedDate = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;
    const formattedTime = `${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}:${String(date.getSeconds()).padStart(2, '0')}`;
    return `${formattedDate} ${formattedTime}`;
  };

  if (loading) {
    return (
      <Center style={{ height: '100vh' }}>
        <Loader />
      </Center>
    );
  }

  return (
    <Box>
      {error && <Text color="red">{error}</Text>}
      {success && <Text color="green">{success}</Text>}
      {isMember ? (
        !isCreator && (
          <Button onClick={handleCircleUnregister} color="red">
            サークルを脱退
          </Button>
        )
      ) : (
        <Button onClick={handleCircleRegister}>サークルに加入</Button>
      )}
      {isMember && (
        <>
          <Textarea
            placeholder="今どうしてる?"
            value={replyContent}
            onChange={(e) => setReplyContent(e.currentTarget.value)}
          />
          <FileInput
            onChange={handleImageChange}
            accept="image/*"
            style={{ marginTop: '10px' }}
          />
          {imagePreview && <Image src={imagePreview} alt="Preview" style={{ maxWidth: '100%', marginTop: '10px' }} />}
          <Button onClick={() => createPost(replyContent)} disabled={imageOk || (!replyContent && !tweetImage)} style={{ marginTop: '10px' }}>
            投稿する
          </Button>
          {allTweets.map((tweet: CircleTweet, index: number) => (
            <Box key={index} mb="lg">
              <Grid>
                <Avatar src={tweet.avatar_url || defaultAvatar} alt="Profile" size={50} radius="xl" />
                <Grid.Col>
                  <Text size='lg' weight={700}>{tweet.username}</Text>
                  <Text size="sm" color="gray">{formatDateTime(tweet.time)}</Text>
                  {tweet.image && <Image src={tweet.image} style={{ maxWidth: '100%', marginTop: '10px' }} alt="Tweet Image" />}
                  <HashtagText text={tweet.content} />
                  <LikeButton
                    tweetid={tweet.tweetid}
                    initialLike={tweet.like}
                    initialIsLiked={tweet.isLiked}
                    onLikeChange={() => {}}
                  />
                  <ReplyList tweetId={tweet.tweetid} onHashtagsExtracted={() => {}} />
                </Grid.Col>
              </Grid>
            </Box>
          ))}
        </>
      )}
    </Box>
  );
};

export default CirclePage;
