import React, { useEffect, useState } from 'react';
import LikeButton from './Like_post.tsx';
import { fetchLikedTweets } from './Like_post.tsx';
import { fireAuth } from '../firebase.ts';

interface Reply {
  replyid: string;
  tweetid: string;
  replycontent: string;
  username: string;
  time: string;
  like: number;
  isLiked: boolean;
}

interface ReplyListProps {
  tweetId: string;
}

const ReplyList: React.FC<ReplyListProps> = ({ tweetId }) => {
  const [replies, setReplies] = useState<Reply[]>([]);
  const [likedTweets, setLikedTweets] = useState<string[]>([]);

  useEffect(() => {
    const fetchReplies = async () => {
      try {
        if (fireAuth.currentUser) {
          const likedTweetsData = await fetchLikedTweets(fireAuth.currentUser.uid);
          setLikedTweets(likedTweetsData);
          console.log(likedTweetsData)
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/searchreply?tweetid=${tweetId}`);
        const data = await response.json();
        console.log(data);
        const replys: Reply[] =[];
        if(data){
          const myreplys = data.map((reply: any) => ({
                replyid: reply.replyid,
                username: reply.username,
                time: reply.time,
                content: reply.replycontent,
                like: reply.like,
                isLiked: likedTweetsData.includes(reply.tweetid),
          }))
          replys.push(...myreplys)
        }
        }
      } catch (error) {
        console.error('Error fetching replies:', error);
      }
    };

    fetchReplies();
  },);

  const handleLikeChange = (replyid: string, isLiked: boolean, likeCount: number) => {
    setLikedTweets(prev => isLiked ? [...prev, replyid] : prev.filter(id => id !== replyid));
    setReplies(prevTweets =>
      prevTweets.map(reply =>
        reply.replyid === replyid ? { ...reply, like: likeCount, isLiked } : reply
      )
    );
  };

  const formatDateTime = (dateTime: string) => {
    const date = new Date(dateTime);
    const formattedDate = `${date.getFullYear()}-${String(date.getMonth() + 1).padStart(2, '0')}-${String(date.getDate()).padStart(2, '0')}`;
    const formattedTime = `${String(date.getHours()).padStart(2, '0')}:${String(date.getMinutes()).padStart(2, '0')}:${String(date.getSeconds()).padStart(2, '0')}`;
    return `${formattedDate} ${formattedTime}`;
  };

  return (
    <div>
      {replies.map((reply) => (
        <div key={reply.replyid}>
          <p>{reply.username}</p>
          <p>{reply.replycontent}</p>
          <p>{formatDateTime(reply.time)}</p>
          <p>Likes: {reply.like}</p>
          <LikeButton
            tweetid={reply.replyid}
            initialLike={reply.like}
            initialIsLiked={reply.isLiked} // ここを適切に変更してください
            onLikeChange={() => {handleLikeChange}} // ここを適切に変更してください
          />
        </div>
      ))}
    </div>
  );
};

export default ReplyList;
