import { fireAuth } from '../firebase.ts';
import React, { useState, useEffect } from 'react';
import Contentform from './Newpost.tsx';
import { getPosts } from './Postapi.tsx';
import MyProfile from './MyProfile_setting.tsx';

interface Post {
  id: string;
  content: string;
  likes: number;
}

const Post_App: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [profileData, setProfileData] = useState<{ name: string; age: number; username: string; avatar: string } | null>(null);
  const [showProfile, setShowProfile] = useState<boolean>(false);

  useEffect(() => {
    const fetchPosts = async () => {
      const posts = await getPosts();
      setPosts(posts);
    };

    const fetchUserProfile = async () => {
      if (fireAuth.currentUser) {
        const response = await fetch(
          `https://hackathon-ro2txyk6rq-uc.a.run.app/search?uid=${fireAuth.currentUser?.uid}`
        );
        const data = await response.json();
        console.log(data);
        if (data && data.length > 0) {
          setProfileData(data[0]);
        } else {
          setProfileData(null);
        }
      }
    };

    fetchPosts();
    fetchUserProfile();
  }, []);

  const createPost = async (content: string) => {
    return new Promise<void>(async (resolve, reject) => {
      const firebaseUID = fireAuth.currentUser?.uid;
      let errormessage = "";
      if (!content) {
        alert("tweet is empty");
        errormessage = "tweet is empty";
      }
      if (errormessage) {
        console.log(errormessage);
        reject(errormessage);
        return;
      }
      try {
        const response = await fetch(
          "https://hackathon-ro2txyk6rq-uc.a.run.app/post",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              content: content,
              uid: firebaseUID,
            }),
          }
        );
        if (!response.ok) {
          const errorData = await response.json();
        console.error("Failed to tweet:", errorData);
          alert("failed to tweet");
          errormessage = "failed to tweet";
          reject(errormessage);
          return;
        } else {
          console.log(firebaseUID);
          resolve();
        }
      } catch (error) {
        console.error("failed tweet", error);
        alert("failed tweet");
        reject(error);
      }
    });
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  const handleProfileClick = () => {
    setShowProfile(!showProfile);
  };

  return (
    <div>
      <h1>Twitter Clone</h1>
      <div className="sidebar">
        <img src={profileData.avatar} alt="Profile" onClick={handleProfileClick} style={{ cursor: 'pointer' }} />
      </div>
      {showProfile ? (
        <MyProfile profileData={profileData} />
      ) : (
        <>
          <Contentform onNewPost={createPost} />
          <div>
            {posts.map(post => (
              <div key={post.id}>
                <p>{post.content}</p>
                <p>{post.likes} Likes</p>
              </div>
            ))}
          </div>
        </>
      )}
    </div>
  );
            }

export default Post_App;
