import React, { useState, useEffect } from 'react';

interface Tweet {
  content: string;
}

interface HashtagCount {
  [key: string]: number;
}

const HashtagRanking: React.FC<{ tweets: Tweet[] }> = ({ tweets }) => {
  const [hashtagCounts, setHashtagCounts] = useState<HashtagCount>({});

  useEffect(() => {
    const counts: HashtagCount = {};
    tweets.forEach(tweet => {
      const hashtags = tweet.content.match(/#[^\s]+/g) || [];
      hashtags.forEach(hashtag => {
        if (!counts[hashtag]) {
          counts[hashtag] = 0;
        }
        counts[hashtag]++;
      });
    });
    setHashtagCounts(counts);
  }, [tweets]);

  const sortedHashtags = Object.entries(hashtagCounts).sort((a, b) => b[1] - a[1]);

  return (
    <div>
      <h2>トレンド</h2>
      <ul>
        {sortedHashtags.map(([hashtag, count], index) => (
          <li key={index}>{hashtag}: {count}件のツイート</li>
        ))}
      </ul>
    </div>
  );
};

export default HashtagRanking;
