import React, { useEffect, useState } from 'react';
import { Box, Text, Center, List, Title } from '@mantine/core';
import { fireAuth } from '../firebase.ts';

interface Circle {
  circleid: string;
  circlename: string;
  creater: string;
}

const CircleList = () => {
  const [circles, setCircles] = useState<Circle[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCircles = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcircle?uid=${fireAuth.currentUser?.uid}`);
        if (response.ok) {
          const data = await response.json();
          console.log('Fetched circles:', data); // デバッグ用ログ
          setCircles(data);
        } else {
          setError('サークルの取得に失敗しました');
        }
      } catch (err) {
        setError('サークルの取得に失敗しました');
      } finally {
        setLoading(false);
      }
    };

    fetchCircles();
  }, []);

  useEffect(() => {
    console.log('Circles state updated:', circles); // デバッグ用ログ
  }, [circles]);

  if (loading) {
    return (
      <Center style={{ height: '100vh' }}>
        <Text>Loading...</Text>
      </Center>
    );
  }

  if (error) {
    return (
      <Center style={{ height: '100vh' }}>
        <Text color="red">{error}</Text>
      </Center>
    );
  }

  return (
    <Box>
      <Title order={2}>あなたのサークル</Title>
      <List spacing="sm" size="sm" mb="lg" center>
        {circles.map((circle) => (
          <List.Item key={circle.circleid}>
            <Text size="lg">{circle.circlename}</Text>
          </List.Item>
        ))}
      </List>
    </Box>
  );
};

export default CircleList;
