import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Text, Center, Loader, Button } from '@mantine/core';
import { fireAuth } from '../firebase.ts';

const CirclePage = () => {
  const { circleid } = useParams();
  const [circle, setCircle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');
  const [success, setSuccess] = useState('');
  const [isMember, setIsMember] = useState<boolean>(false);
  const [hidden, setHidden] = useState<boolean>(false);

  useEffect(() => {
    const fetchCircles = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcircle?uid=${fireAuth.currentUser?.uid}`);
        if (response.ok) {
          const data = await response.json();
          for (let i = 0; i < data.length; i++) {
            if (circleid === data[i].circleid) {
              setHidden(true);
              setIsMember(true);
            }
          }
        }
      } catch (error) {
        console.log(error);
      }
    };

    const fetchCircle = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcirclebyid?circleid=${circleid}`);
        if (response.ok) {
          const data = await response.json();
          setCircle(data);
        } else {
          setError('サークルの取得に失敗しました');
        }
      } catch (err) {
        setError('サークルの取得に失敗しました');
      } finally {
        setLoading(false);
      }
    };

    fetchCircles();
    fetchCircle();
  }, [circleid]);

  const handleCircleRegister = async () => {
    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/postcirclemember', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleID: circleid, uid: fireAuth.currentUser?.uid }),
      });

      if (response.ok) {
        setSuccess('サークルに加入しました');
        setError('');
        setHidden(true); // 加入後にボタンを隠す
        setIsMember(true);
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの加入に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの加入に失敗しました');
      setSuccess('');
    }
  };

  const handleCircleUnregister = async () => {
    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/stopcircle', {
        method: 'DELETE',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleid: circleid, uid: fireAuth.currentUser?.uid }),
      });

      if (response.ok) {
        setSuccess('サークルを脱退しました');
        setError('');
        setHidden(false);
        setIsMember(false);
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの脱退に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの脱退に失敗しました');
      setSuccess('');
    }
  };

  if (loading) {
    return (
      <Center style={{ height: '100vh' }}>
        <Loader />
      </Center>
    );
  }

  return (
    <Box>
      {error && <Text color="red">{error}</Text>}
      {success && <Text color="green">{success}</Text>}
      {!hidden && (
        <Button onClick={handleCircleRegister}>サークルに加入</Button>
      )}
      {isMember && (
        <Button onClick={handleCircleUnregister} color="red">サークルを脱退</Button>
      )}
      {/* サークルに関する他の情報を表示 */}
    </Box>
  );
};

export default CirclePage;
