import React, { useEffect, useState } from 'react';
import { useParams } from 'react-router-dom';
import { Box, Text, Center, Loader, Button } from '@mantine/core';
import { fireAuth } from '../firebase.ts';

const CirclePage = () => {
  const { circleid } = useParams();
  const [circle, setCircle] = useState(null);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');


  const handleCircleRegister = async () => {
    const [error, setError] = useState('');
    const [success, setSuccess] = useState('');
    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/postcirclemember', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify({ circleID: circleid, creater: fireAuth.currentUser?.uid }),
      });

      if (response.ok) {
        setSuccess('サークルに加入しました');
        setError('');
      } else {
        const result = await response.json();
        setError(result.message || 'サークルの加入に失敗しました');
        setSuccess('');
      }
    } catch (err) {
      setError('サークルの加入に失敗しました');
      setSuccess('');
    }
  };

  if (loading) {
    return (
      <Center style={{ height: '100vh' }}>
        <Loader />
      </Center>
    );
  }


  return (
    <Box>
      <Button onClick={handleCircleRegister}></Button>
      {/* サークルに関する他の情報を表示 */}
    </Box>
  );
};

export default CirclePage;
