import React, { useState, useEffect } from 'react';
import {fireAuth} from './firebase.ts';
import { User,signOut } from 'firebase/auth';
import Post from "./Post/PostUI.tsx";
import NewPost from  "./Post/Newpost.tsx";
import ProfileSetup from './Profile/Profilesetup.tsx';
import { getPosts, createPost, likePost} from './Post/Postapi.tsx';
import Signup_in_App from './Signup_in/Signup_in_App.tsx';


export interface Post {
  id: string;
  content: string;
  likes: number;
}

const App: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [profileData, setProfileData] = useState<any>(null);
  const [profileComplete, setProfileComplete] = useState<any>(null);

  useEffect(() => {
    const unsubscribe = fireAuth.onAuthStateChanged(async user => {
      setUser(user);
      if (user) {
        handleAuthSuccess();
      }
    });
    fetchPosts();
    return () => unsubscribe();
  }, []);

  const fetchPosts = async () => {
    const posts = await getPosts();
    setPosts(posts);
  };

  const handleNewPost = async (content: string) => {
    await createPost(content);
    fetchPosts();
  };

  const handleLikePost = async (postId: string) => {
    await likePost(postId);
    fetchPosts();
  };

  const handleAuthSuccess = async() => {
    if (fireAuth.currentUser) {
      const response = await fetch(
        `https://hackathon-ro2txyk6rq-uc.a.run.app/search?uid=${fireAuth.currentUser.uid}`);
      const data = await response.json();
      console.log(data);
      if (data && data.length > 0) {
        setProfileData(data[0]); // 配列の最初の要素にアクセス
      } else {
        setProfileData(null); // データがない場合はnullを設定
      }
    }
  };

  const handleProfileComplete = (profileData: any) => {
    setProfileComplete(profileData);
  };

  if (!user) {
    return <Signup_in_App onAuthSuccess={handleAuthSuccess} />;
  }

  if (!profileData) {
    return <ProfileSetup onProfileComplete={handleProfileComplete} />;
  }

  const signOutWithGoogle = (): void => {
    signOut(fireAuth).then(() => {
      alert("ログアウトしました");
    }).catch(err => {
      alert(err);
    });
  };

  return (
    <div>
      <h1>Twitter Clone</h1>
      <NewPost onNewPost={handleNewPost} />
      {posts.map(post => (
        <Post key={post.id} post={post} onLike={handleLikePost} />
      ))}
      <div>
        <p>ログイン中</p>
        <p>名前: {profileData.name}</p>
        <p>年齢: {profileData.age}</p>
        <p>ユーザー名: {profileData.username}</p>
        <button onClick={signOutWithGoogle}>ログアウト</button>
      </div>
    </div>
  );
}

export default App;
