import { fireAuth } from '../firebase.ts';
import React, { useState, useEffect, useCallback } from 'react';
import Contentform from './Newpost.tsx';
import { getPosts } from './Postapi.tsx';
import { Link, useNavigate } from 'react-router-dom';
import debounce from 'lodash.debounce';
import "./Post_App.css"
import defaultAvatar from '../assets/default_user.png';

interface Post {
  id: string;
  content: string;
  likes: number;
}

const Post_App: React.FC = () => {
  const [posts, setPosts] = useState<Post[]>([]);
  const [profileData, setProfileData] = useState<{ name: string; age: number; username: string; avatar_url: string;} | null>(null);
  const navigate = useNavigate();
  const [searchTerm, setSearchTerm] = useState('');
  const [searchResults, setSearchResults] = useState<any[]>([]);
  const [noResults, setNoResults] = useState(false);
  const [loading, setLoading] = useState(true);
  const [avatarURL, setAvatarURL] = useState<string | null>(null);
  const [defaultuser, setDefaultuser] = useState<string | null>(defaultAvatar);
  const [userLoading, setuserLoading] = useState<string | null>(null);
  const [OtherTweetData, setOtherTweetData] = useState<any>(null);

  useEffect(() => {
    const fetchPosts = async () => {
      const fetchedPosts = await getPosts();
      setPosts(fetchedPosts);;
    };
    const fetchUserProfile = async () => {
      if (fireAuth.currentUser) {
        const response = await fetch(
          `https://hackathon-ro2txyk6rq-uc.a.run.app/search?uid=${fireAuth.currentUser?.uid}`
        );
        const data = await response.json();
        console.log(data);
        if (data && data.length > 0) {
          setProfileData(data[0]);
        } else {
          setProfileData(null);
        }
      }
    };

    const fetchAvatarURL = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/searchAvatar?uid=${fireAuth.currentUser?.uid}`);
        const data = await response.json();
        console.log(data.avatar_url)
        if (data.avatar_url != ""){
        setAvatarURL(`https://hackathon-ro2txyk6rq-uc.a.run.app${data.avatar_url}`);
        setLoading(false);
        }else{
        setAvatarURL(defaultAvatar);
        setLoading(false);
        }
      } catch (error) {
        console.error('Failed to fetch avatar URL:', error);
        setLoading(false);
      }
    };


    fetchPosts();
    fetchUserProfile();
    fetchAvatarURL();
  }, []);

  const handleOtherTweet = async(uid: string) => {
    const response = await fetch(
      `https://hackathon-ro2txyk6rq-uc.a.run.app/mytweet?uid=${uid}`);
    const data = await response.json();
    console.log(data);
    if (data && data.length > 0) {
      setOtherTweetData(data);
    } else {
      setOtherTweetData(null); // データがない場合はnullを設定
    }
  }

  const check_get_IfFollowing = async (uid: string) => {
    try {
      const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/searchfollow?followuser=${fireAuth.currentUser?.uid}`);
      const data = await response.json();
      console.log(data)
      if (data) {
        for(let i=0; i < data.length; i++){
          handleOtherTweet(data[i].followeruid)
        }
      }
    } catch (error) {
      console.error('Failed to check follow status:', error);
    }
  }

  const debouncedSearch = useCallback(debounce(async (query: string) => {
    if (query.length > 0) {
      const response = await fetch(
        `https://hackathon-ro2txyk6rq-uc.a.run.app/searchUsers?username=${query}`
      );
      const data = await response.json();
      if (data && data.length > 0) {
        setSearchResults(data);
        setNoResults(false);
      } else {
        setSearchResults([]);
        setNoResults(true);
      }
    } else {
      setSearchResults([]);
      setNoResults(false);
    }
  }, 300), []);

  useEffect(() => {
    if (searchTerm === '') {
      setSearchResults([]);
      setNoResults(false);
    }
  }, [searchTerm]);

  const createPost = async (content: string) => {
    return new Promise<void>(async (resolve, reject) => {
      const firebaseUID = fireAuth.currentUser?.uid;
      let errormessage = "";
      if (!content) {
        alert("tweet is empty");
        errormessage = "tweet is empty";
      }
      if (errormessage) {
        console.log(errormessage);
        reject(errormessage);
        return;
      }
      try {
        const response = await fetch(
          "https://hackathon-ro2txyk6rq-uc.a.run.app/post",
          {
            method: "POST",
            headers: {
              "Content-Type": "application/json",
            },
            body: JSON.stringify({
              content: content,
              uid: firebaseUID,
            }),
          }
        );
        if (!response.ok) {
          const errorData = await response.json();
        console.error("Failed to tweet:", errorData);
          alert("failed to tweet");
          errormessage = "failed to tweet";
          reject(errormessage);
          return;
        } else {
          console.log(firebaseUID);
          resolve();
        }
      } catch (error) {
        console.error("failed tweet", error);
        alert("failed tweet");
        reject(error);
      }
    });
  };

  if (!profileData) {
    return <div>Loading...</div>;
  }

  if (loading) {
    return <div>Loading...</div>;
  }

  const handleProfileClick = () => {
    navigate("/profile");
  };


  const handleSearch = async (e: React.ChangeEvent<HTMLInputElement>) => {
    const query = e.target.value;
    setuserLoading("loading");
    setSearchTerm(query);
    debouncedSearch(query);
    setuserLoading(null);
  };

  const handleUserClick = (uid: string) => {
    navigate(`/userProfile/${uid}`);
  };

  return (
    <div>
      <h1>Twitter Clone</h1>
      {userLoading ? (
        <div>loading...</div>
        ) : (
        <></>
      )}
      <input
        type="text"
        placeholder="ユーザーネームを検索"
        value={searchTerm}
        onChange={handleSearch}
        style={{ width: '50%', margin: '0 auto', display: 'block' }}
      />
      <div>
        {searchResults.map(user => (
          <div key={user.uid} onClick={() => handleUserClick(user.uid)} style={{ cursor: 'pointer' }}>
          <p><img src={avatarURL || defaultAvatar} alt="Profile" onClick={handleProfileClick} style={{ width: '100px', height: '100px', cursor: 'pointer', borderRadius: '50%' }} />{user.username}</p>
        </div>
        ))}
        {noResults && <p>該当無し</p>}
      </div>
      <div className="sidebar">
      <img src={avatarURL || defaultAvatar} alt="Profile" onClick={handleProfileClick} style={{ width: '100px', height: '100px', cursor: 'pointer', borderRadius: '50%' }} />
      </div>
      <Contentform onNewPost={createPost} />
      <div>
        {posts.map(post => (
          <div key={post.id}>
            <p>{post.content}</p>
            <p>{post.likes} Likes</p>
          </div>
        ))}
      </div>
    </div>
  );
};


export default Post_App;
