import React, { useState, useEffect } from 'react';
import {fireAuth} from './firebase';
import { User } from 'firebase/auth';
import Post from "./Post/PostUI";
import NewPost from  "./Post/Newpost";
import Login from './Login/Login';
import ProfileSetup from './Profile/Profilesetup';
import { getPosts, createPost, likePost, isProfileComplete } from './api';

export interface Post {
  id: string;
  content: string;
  likes: number;
}

const App: React.FC = () => {
  const [user, setUser] = useState<User | null>(null);
  const [posts, setPosts] = useState<Post[]>([]);
  const [profileComplete, setProfileComplete] = useState<boolean>(false);

  useEffect(() => {
    const unsubscribe = fireAuth.onAuthStateChanged(async user => {
      setUser(user);
      if (user) {
        const isComplete = await isProfileComplete(user.uid);
        setProfileComplete(isComplete);
      }
    });
    fetchPosts();
    return () => unsubscribe();
  }, []);

  const fetchPosts = async () => {
    const posts = await getPosts();
    setPosts(posts);
  };

  const handleNewPost = async (content: string) => {
    await createPost(content);
    fetchPosts();
  };

  const handleLikePost = async (postId: string) => {
    await likePost(postId);
    fetchPosts();
  };

  const handleAuthSuccess = async() => {
    if (fireAuth.currentUser) {
      const isComplete = await isProfileComplete(fireAuth.currentUser.uid);
      setProfileComplete(isComplete);
    }
  };

  const handleProfileComplete = () => {
    setProfileComplete(true);
  };

  if (!user) {
    return <Login onAuthSuccess={handleAuthSuccess} />;
  }

  if (!profileComplete) {
    return <ProfileSetup onProfileComplete={handleProfileComplete} />;
  }

  return (
    <div>
      <h1>Twitter Clone</h1>
      <NewPost onNewPost={handleNewPost} />
      {posts.map(post => (
        <Post key={post.id} post={post} onLike={handleLikePost} />
      ))}
    </div>
  );
}

export default App;
