import React, { useEffect, useState } from 'react';
import { Box, Text, Center, List, Title } from '@mantine/core';
import { fireAuth } from '../firebase.ts';
import { Link } from 'react-router-dom';

interface Circle {
  circleid: string;
  circlename: string;
  creater: string;
}

interface CircleListProps {
  circles: Circle[];
}

const CircleList: React.FC<CircleListProps> = ({ circles }) => {
  const [allCircles, setAllCircles] = useState<Circle[]>([]);
  const [loading, setLoading] = useState(true);
  const [error, setError] = useState('');

  useEffect(() => {
    const fetchCircles = async () => {
      try {
        const response = await fetch(`https://hackathon-ro2txyk6rq-uc.a.run.app/getcircle?uid=${fireAuth.currentUser?.uid}`);
        if (response.ok) {
          const data = await response.json();
          console.log('Fetched circles:', data);
          setAllCircles(data);
        } else {
          setError('サークルの取得に失敗しました');
        }
      } catch (err) {
        setError('サークルの取得に失敗しました');
      } finally {
        setLoading(false);
      }
    };

    fetchCircles();
  }, []);

  useEffect(() => {
    setAllCircles((prevCircles) => [...prevCircles, ...circles]);
  }, [circles]);

  if (loading) {
    return (
      <Center style={{ height: '100vh' }}>
        <Text>Loading...</Text>
      </Center>
    );
  }

  if (error) {
    return (
      <Center style={{ height: '100vh' }}>
        <Text color="red">{error}</Text>
      </Center>
    );
  }

  return (
    <Box>
      <Title order={2}>あなたのサークル</Title>
      <List spacing="sm" size="sm" mb="lg" center>
        {allCircles.map((circle) => (
          <List.Item key={circle.circleid}>
            <Link to={`/circle/${circle.circleid}`}>
              <Text>{circle.circlename}</Text>
            </Link>
          </List.Item>
        ))}
      </List>
    </Box>
  );
};

export default CircleList;
