import React, { useState } from 'react';
import { fireAuth } from '../firebase.ts';

interface ReplyFormProps {
  tweetId: string;
  userName: string;
  onReplyPosted: () => void;
}

const ReplyForm: React.FC<ReplyFormProps> = ({ tweetId, userName, onReplyPosted }) => {
  const [replyContent, setReplyContent] = useState('');

  const handleReplySubmit = async () => {
    const user = fireAuth.currentUser;
    if (!user) {
      console.error('User not authenticated');
      return;
    }

    const reply = {
      replycontent: replyContent,
      uid: user.uid,
      tweetid: tweetId,
      username: userName, // 現在ログインしているユーザーのユーザー名を設定
    };

    try {
      const response = await fetch('https://hackathon-ro2txyk6rq-uc.a.run.app/reply', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(reply),
      });

      if (response.ok) {
        const data = await response.json();
        onReplyPosted();
        setReplyContent('');
      } else {
        console.error('Failed to post reply');
      }
    } catch (error) {
      console.error('Error posting reply:', error);
    }
  };

  return (
    <div>
      <input
        type="text"
        value={replyContent}
        onChange={(e) => setReplyContent(e.target.value)}
        placeholder="返信を入力"
      />
      <button onClick={handleReplySubmit}>返信</button>
    </div>
  );
};

export default ReplyForm;
